--Create User table--
CREATE TABLE "user" (
    "id" SERIAL PRIMARY KEY,
    "username" VARCHAR (80) NOT NULL,
    "password" VARCHAR (1000) NOT NULL,
    "access_level" INT DEFAULT 1,
    "token" VARCHAR(40),
    "expiration" TIMESTAMP
);

